let _id = [],
  imageUrl = [],
  productName = [],
  description = [],
  varnish = [],
  quantity = [],
  price = [],
  i = [];
fetch("http://localhost:3000/api/furniture/")
  .then((i) => i.json())
  .then((n) => {
    let t = "";
    for (i = 0; i < n.length; i++)
      n.forEach((i, n) => {
        (_id[n] = i._id),
          (imageUrl[n] = i.imageUrl),
          (productName[n] = i.name),
          (description[n] = i.description),
          (varnish[n] = i.varnish),
          (quantity[n] = i.quantity),
          (price[n] = i.price);
      }),
        (t += `\n     <div class="global_articles" id="furnitures">\n        <figure class="product">\n            <a href="./view/product.html?id=${
          _id[i]
        }">\n           <img src=${
          imageUrl[i]
        }  class="products_container" sizes="100vw" alt="image_product"/> </a>\n         <figcaption class="description">\n            <article>\n         <h3>${
          productName[i]
        }</h3>\n       \n           <p>${
          price[i] / 100
        }â‚¬</p>\n        \n        </article>\n       \n           </figcaption>\n        </figure>\n        </div>\n        `),
        (document.getElementById("furnitures").innerHTML = t);
  })
  .catch((i) => {});
