let _id = [],
  imageUrl = [],
  productName = [],
  description = [],
  varnish = [],
  quantity = [],
  price = [],
  i = [];
fetch("http://localhost:3000/api/furniture/")
  .then((res) => res.json())
  .then((array_furnitures) => {
    console.log(array_furnitures);
    let cardArticles = "";
    for (i = 0; i < array_furnitures.length; i++)
      array_furnitures.forEach((element, i) => {
        (_id[i] = element._id),
          (imageUrl[i] = element.imageUrl),
          (productName[i] = element.name),
          (description[i] = element.description),
          (varnish[i] = element.varnish),
          (quantity[i] = element.quantity),
          (price[i] = element.price);
      }),
        (cardArticles += `
     
      </section>
      <div class="products">
        <figure class="product">
         <a href="./view/product.html?id=${_id[i]}">
        <img src=${
          imageUrl[i]
        } class="products_container" alt="image_product"/> </a>
        <div class="description">
        <p>${productName[i]}</p>
        <div class="price">
        <p>${price[i] / 100}€</p>
        </div>
      
        </figure>
        </div>
        `);
    document.getElementById("furnitures").innerHTML = cardArticles;
  })
  .catch((error) => {
    let productsContainer = document.getElementById("meubles_chêne");
    productsContainer.innerHTML =
      "Oups ! Merci de demarrer votre serveur au port 300 <br> si le problème persiste, contactez-nous";
  });
